import React, { useState } from "react";
import { Card, Image, Button, Input } from "antd";
import { useNFTBalance } from "hooks/useNFTBalance";
import { openseaConfig } from "helpers/openseaConfig";
import { useMoralis } from "react-moralis";
import { useMoralisDapp } from "../providers/MoralisDappProvider/MoralisDappProvider";
const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
  Button: {
    display: "flex",
    justifyContent: "center",
    margin: "auto",
    backgroundColor: "#d1ffab",
    borderRadius: "14px",
    minWidth: "100px",
    marginTop: "10px"
  },
  Price: {
    textAlign: "center",
    margin: "10px 0 10px 0",
    fontWeight: "500"
  },
  OwnNft: {
    display: "flex",
    fontSize: "30px",
    marginTop:"50%"
  }
};


function NFTBalance(options) {
  const { Moralis } = useMoralis();
  const [ offerPrice, setOfferPrice ] = useState(0);
  const { NFTBalanceSell, NFTBalance } = useNFTBalance();
  const { walletAddress } = useMoralisDapp();

  const buy = async (order) => {
    await Moralis.Plugins.opensea.fulfillOrder({
      network: openseaConfig.network,
      userAddress: walletAddress,
      order: order,
    });
  }

  const createBuyOrder = async (tokenAddress, tokenId, tokenType, price) => {
    await Moralis.Plugins.opensea.createBuyOrder({
      network: openseaConfig.network,
      tokenAddress: tokenAddress,
      tokenId: tokenId,
      tokenType: tokenType,
      amount: price,
      userAddress: walletAddress,
      paymentTokenAddress: "0xc778417e063141139fce010982780140aa0cd5ab",
    });
  };

  return (
      <>
        <div style={styles.NFTs}>
          {NFTBalanceSell &&
          NFTBalanceSell.map((nft, index) => (
              <Card
                  hoverable
                  style={{ width: 240, border: "2px solid #e7eaf3" }}
                  cover={
                    <Image
                        src={nft?.imageUrl || "error"}
                        fallback="data:image/png;base64,"
                    />
                  }
                  key={index}
              >
                <Meta title={nft?.name} description={nft?.assetContract.address} />
                <Input
                    sx={{ borderColor: "1px solid black" }}
                    placeholder="Please set offer price"
                    onChange={(e) =>
                        setOfferPrice({ ...offerPrice, price: e.target.value })
                    }
                />
                <Button style={styles.Button} onClick={ ()=>{ createBuyOrder(nft?.assetContract.address, nft?.tokenId, nft?.assetContract.schemaName, offerPrice.price) } }>Make offer</Button>
                {nft?.buyOrders[0] && <div style={styles.Price}> Price : { nft?.orders[0].currentPrice / 1000000000000000000 } {nft?.orders[0].paymentTokenContract.symbol}</div>}
                {nft?.buyOrders[0] && <Button style={styles.Button}  onClick={ ()=>{ buy(nft?.orders[0]) } }>Buy</Button> }
              </Card>
          ))}

          {NFTBalance &&
          NFTBalance.map((nft, index) => (
              <Card
                  hoverable
                  style={{ width: 240, border: "2px solid #e7eaf3" }}
                  cover={
                    <Image
                        src={nft?.metadata.image || "error"}
                        fallback="data:image/png;base64,"
                    />
                  }
                  key={index}
              >
                <Meta title={nft?.metadata.name} description={nft?.token_address} />
                <div style={styles.OwnNft}>Your NFT</div>
              </Card>
          ))}
        </div>
      </>
  );
}

export default NFTBalance;
